-- Author:		Shafiul Alam
-- Project:		MONAHRQ 6.0 Build 1
-- Create date: 12-22-2014
-- Description:	This is the update script from older MONAHRQ 5.0 Reports table to the new 
--              MONAHRQ 6.0 database schema.
--				'Reports Data'
-- =============================================*/

BEGIN TRY 

IF OBJECT_ID('tempdb..#ReportsForeignKeys') IS NOT NULL
DROP TABLE #ReportsForeignKeys

SELECT identity(INT,1,1) rownum,
OBJECT_NAME(fk.parent_object_id) parentTable,NAME 
INTO #ReportsForeignKeys
FROM sys.foreign_keys fk
WHERE fk.referenced_object_id= OBJECT_ID('Reports')

DECLARE @LoopIterator INT=1,
		@LoopCount INT= (SELECT COUNT(1) FROM #ReportsForeignKeys),
		@sql VARCHAR(8000)
		
WHILE (@LoopIterator<=@LoopCount)
BEGIN
	
	SELECT @sql='ALTER TABLE [dbo].['+parentTable+'] DROP CONSTRAINT '+NAME
	FROM #ReportsForeignKeys i
	WHERE rownum=@LoopIterator
	
	EXEC (@sql)
	
	SELECT @sql='IF NOT EXISTS (SELECT 1 FROM sys.[columns] c WHERE c.name=''ReportsId'' AND c.[object_id]=OBJECT_ID('''+parentTable+'''))
	             ALTER TABLE [dbo].['+parentTable+'] ADD ReportsId INT'
	FROM #ReportsForeignKeys i
	WHERE rownum=@LoopIterator
	
	EXEC (@sql)
	
	SET @LoopIterator=@LoopIterator+1
END

IF NOT EXISTS (SELECT 1 FROM sys.[columns] c WHERE c.name='ReportsId' AND c.[object_id]=OBJECT_ID('Reports'))
ALTER TABLE Reports add ReportsId INT IDENTITY(1,1)

/*******************************************
 *  Updating new Identity Report Id to
 *  Foreign key columns
 *******************************************/

SET @LoopIterator=1

WHILE (@LoopIterator<=@LoopCount)
BEGIN
	
	SELECT @sql='UPDATE  [dbo].['+parentTable+'] SET ReportsId = r.ReportsId
                 FROM [dbo].['+parentTable+'] a
				 INNER JOIN Reports r
				 ON r.Id = a.Report_Id'
	FROM #ReportsForeignKeys i
	WHERE rownum=@LoopIterator
	
	EXEC (@sql)
	
	SET @LoopIterator=@LoopIterator+1
END

/*******************************************
 *  Drop,Rename and create Primary Key in Reports table
 *******************************************/

IF EXISTS (SELECT 1 FROM sys.[columns] c WHERE c.name='ID' AND c.[object_id]=OBJECT_ID('Reports'))
BEGIN
	IF EXISTS (SELECT 1 FROM sys.key_constraints kc WHERE object_name(kc.parent_object_id)='Reports' AND kc.[type]='PK')
	BEGIN	
		SELECT @sql='ALTER TABLE Reports DROP CONSTRAINT '+kc.name
		  FROM sys.key_constraints kc WHERE object_name(kc.parent_object_id)='Reports' AND kc.[type]='PK'
		EXEC (@sql)  
	END
ALTER TABLE Reports DROP COLUMN Id;
EXEC sp_rename 'dbo.Reports.ReportsId', 'Id', 'COLUMN';
ALTER TABLE Reports ADD PRIMARY KEY (Id)

IF(OBJECT_ID(N'ReportsTemp')) IS NOT NULL	
	DROP TABLE ReportsTemp

CREATE TABLE ReportsTemp(
	[Id] [INT] IDENTITY(1,1) NOT NULL,
	[Name] [NVARCHAR](255) NOT NULL,
	[ReportProfile] [NVARCHAR](255) NULL,
	[Category] [NVARCHAR](255) NULL,
	[IsTrending] [BIT] NULL,
	[Audiences] [NVARCHAR](255) NOT NULL,
	[Datasets] [NVARCHAR](MAX) NOT NULL,
	[RequiresCostToChargeRatio] [BIT] NULL,
	[RequiresCmsProviderId] [BIT] NULL,
	[ReportAttributes] [NVARCHAR](255) NULL,
	[Description] [NVARCHAR](MAX) NULL,
	[Footnote] [NVARCHAR](MAX) NULL,
	[ComparisonKeyIconSetName] [NVARCHAR](255) NULL,
	[ReportType] [NVARCHAR](255) NOT NULL,
	[SourceTemplateXml] [XML] NULL,
	[IsDefaultReport] [BIT] NOT NULL DEFAULT(0),
	[IsCustom] [BIT] NOT NULL  DEFAULT(0),
	[Filter] [NVARCHAR](255) NULL,
	[FilterItemsXml] [XML] NULL,
	[InterpretationText] [NVARCHAR](MAX) NULL,
	[ShowInterpretationText] [BIT] NOT NULL  DEFAULT(0),
	[ReportSql] [NVARCHAR](MAX) NULL,
PRIMARY KEY CLUSTERED 
(
	[Id] ASC
)WITH (PAD_INDEX = OFF, STATISTICS_NORECOMPUTE = OFF, IGNORE_DUP_KEY = OFF, ALLOW_ROW_LOCKS = ON, ALLOW_PAGE_LOCKS = ON) ON [PRIMARY]
) ON [PRIMARY] TEXTIMAGE_ON [PRIMARY]

SET IDENTITY_INSERT dbo.ReportsTemp ON 

INSERT INTO	dbo.ReportsTemp
        (Id ,Name ,ReportProfile ,Category ,IsTrending ,Audiences ,Datasets ,RequiresCostToChargeRatio ,
          RequiresCmsProviderId ,ReportAttributes ,Description ,Footnote ,ComparisonKeyIconSetName ,
          ReportType ,SourceTemplateXml ,IsDefaultReport ,IsCustom ,Filter ,FilterItemsXml ,
          InterpretationText ,ShowInterpretationText  
        )
SELECT	 Id ,Name ,ReportProfile ,Category ,0 ,Audiences ,Datasets ,RequiresCostToChargeRatio ,
          RequiresCmsProviderId ,ReportAttributes ,Description ,Footnote ,ComparisonKeyIconSetName ,
          SourceTemplateXml.value(N'/ReportManifest[1]/@Name',  'nvarchar(max)') ,SourceTemplateXml ,IsDefaultReport ,0 ,Filter ,FilterItemsXml ,
          InterpretationText ,ShowInterpretationText  
FROM [dbo].[Reports]

SET IDENTITY_INSERT dbo.ReportsTemp OFF	

IF(OBJECT_ID(N'Reports')) IS NOT NULL
	DROP TABLE [dbo].[Reports]

EXEC sp_rename  [ReportsTemp], [Reports]

END

/*******************************************
 *  Add foreign key
 *******************************************/

SET @LoopIterator=1

WHILE (@LoopIterator<=@LoopCount)
BEGIN
	 
	SELECT @sql='IF (NOT EXISTS (SELECT * FROM sys.foreign_keys WHERE object_id = OBJECT_ID(N''[dbo].['+NAME+']'') AND parent_object_id = OBJECT_ID(N''[dbo].['+parentTable+']''))
	             AND EXISTS (SELECT 1 FROM sys.[columns] c WHERE c.name=''ReportsId'' AND c.[object_id]=OBJECT_ID('''+parentTable+''')))
				BEGIN
				ALTER TABLE '+parentTable+' DROP COLUMN Report_Id;
				EXEC sp_rename ''dbo.'+parentTable+'.ReportsId'', ''Report_Id'', ''COLUMN'';	
				ALTER TABLE [dbo].['+parentTable+']  WITH CHECK ADD  CONSTRAINT ['+NAME+'] FOREIGN KEY([Report_Id])
				REFERENCES [dbo].[Reports] ([Id])
				ALTER TABLE [dbo].['+parentTable+'] CHECK CONSTRAINT ['+NAME+']
				END'
	FROM #ReportsForeignKeys i
	WHERE rownum=@LoopIterator
	
	EXEC (@sql)
	
	SET @LoopIterator=@LoopIterator+1
END

--UPDATE Reports
--SET    ReportType = NAME;

/*******************************************
 *  Alter Reports_ComparisonKeyIconSets column
 *  from VARBINARY to NVARCHAR
 *******************************************/

ALTER TABLE Reports_ComparisonKeyIconSets ALTER COLUMN [BestImage] NVARCHAR(255)
ALTER TABLE Reports_ComparisonKeyIconSets ALTER COLUMN [BelowImage] NVARCHAR(255)
ALTER TABLE Reports_ComparisonKeyIconSets ALTER COLUMN [BetterImage] NVARCHAR(255)
ALTER TABLE Reports_ComparisonKeyIconSets ALTER COLUMN [NotEnoughDataImage] NVARCHAR(255)
ALTER TABLE Reports_ComparisonKeyIconSets ALTER COLUMN [AverageImage] NVARCHAR(255)

/*******************************************
 * Reports_ReportColumns ID column datatype change
 * to INT from UNIQUEIDENTIFIER
 *******************************************/

IF EXISTS (SELECT 1 FROM sys.[columns] c WHERE c.name='Id' AND c.[object_id]=OBJECT_ID('Reports_ReportColumns') 
           AND c.system_type_id=36 )
BEGIN
	IF EXISTS (SELECT 1 FROM sys.key_constraints kc WHERE object_name(kc.parent_object_id)='Reports_ReportColumns' AND kc.[type]='PK')
	BEGIN	
		SELECT @sql='ALTER TABLE Reports_ReportColumns DROP CONSTRAINT '+kc.name
		  FROM sys.key_constraints kc WHERE object_name(kc.parent_object_id)='Reports_ReportColumns' AND kc.[type]='PK'
		EXEC (@sql)  
	END
ALTER TABLE Reports_ReportColumns DROP COLUMN Id
ALTER TABLE Reports_ReportColumns add Id INT IDENTITY(1,1)
ALTER TABLE Reports_ReportColumns ADD PRIMARY KEY (Id)
END


